import React from 'react';

import amar from '../assets/images/amar.jpeg'
import pool from '../assets/images/pool.gif'

class Carousel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 0,
                    title: 'Liverpool',
                    subTitle: 'Champions of Europe',
                    imgSrc: pool,
                    link: 'https://liverpoolfc.com',
                    selected: false
                },
                {
                    id: 1,
                    title: 'Amar',
                    subTitle: 'Student at UCI',
                    imgSrc: amar,
                    link: 'https://www.linkedin.com/in/amartyapadmakumar/',
                    selected: false
                }
            ]

        }
    }

    handleCardClick = (id) => {
        let items = [...this.state.items];

        items[id].selected = items[id].selected ? false : true; 

        items.forEach(item => {
            if (item.id !== id) {
                item.selected = false;
            }
        });

        this.setState({
            items
        });
    }

    render()
    {
        return (
            <p>Carousel Works!</p>
        )
    }

}

export default Carousel;
