import React from 'react'
import Hero from '../components/Hero'
import Content from '../components/Content'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'


class ContactPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            name: '',
            email: '',
            message: '',
            disabled: false,
            emailSent: null
        }
    }

    handleChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
    }

    render() {
        return (
            <div>
                <Hero title = {this.props.title} />
                <Content>
                    <Form onSubmit = {this.handleSubmit}>
                        <Form.Group>
                            <Form.Label htmlfor = "full-name">Full Name</Form.Label>
                            <Form.Control id = 'full-name' name='name' type='text' value={this.state.name} onChange={this.handleChange} />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label htmlfor = "email">Email</Form.Label>
                            <Form.Control id = 'email' name='email' type='email' value={this.state.email} onChange={this.handleChange} />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label htmlfor = "message">message</Form.Label>
                            <Form.Control id = 'message' name='message' as='textarea' rows = '5' value={this.state.message} onChange={this.handleChange} />
                        </Form.Group>

                        <Button className = 'd-inline-block' variant='primary' type='submit' disabled={this.state.disabled}>
                            Send 
                        </Button>

                        {this.state.emailSent && <p className = 'd-inline success-msg'>Email sent succesfully</p>}
                        {!this.state.emailSent && <p className = 'd-inline err-msg'>Email not sent succesfully</p>}
                    </Form>
                </Content>
            </div>
        );
    }
}

export default ContactPage;
