import React from 'react';
import './App.css';
import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';

import Footer from './components/Footer'; 
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';

class App extends React.Component{

	constructor(props)
	{
		super(props);
		this.state = {
				title: 'Amartya Padmakumar',
				headerLinks: [
					{title: 'Home', path: '/'},
					{title: 'About', path: '/about'},
					{title: 'Contact', path: '/contact'}
				],
		home: {
			title: 'Be relentless',
			subTitle: 'Projects that make a difference',
			text: 'Learn more about me below'

		},
		about: {
			title: "A little more about me" 
		},
		contact: {
			title: "Let's talk"
		}

		}
	}

	render()
	{
		return (

		<Router>
			<Container className = "p-0" fluid = {true}>
				<Navbar className = 'border-bottom' bg='transparent' expand = 'lg '>
					<Navbar.Brand> Amartya Padmakumar </Navbar.Brand>
					<Navbar.Toggle className = 'border-0' aria-controls = "navbar-toggle" />
					<Navbar.Collapse id = "navbar-toggle">
						<Nav className = "ml-auto"> 
							<Link className = "nav-link" to= '/'> Home </Link>
							<Link className = "nav-link" to= '/resume'> About </Link>
							<Link className = "nav-link" to= '/contact'> Contact </Link>
						</Nav>
					</Navbar.Collapse>

				</Navbar>
				<Route path='/' exact render={() => <HomePage title = {this.state.home.title} subTitle = {this.state.home.subTitle} text = {this.state.home.text} /> } />
				<Route path='/resume' exact render={() => <AboutPage title = {this.state.about.title} /> } />
				<Route path='/contact' exact render={() => <ContactPage title = {this.state.contact.title} /> } />
				<Footer>

				</Footer>
			</Container>
		</Router>
		);
	}
}

export default App;
